import React from 'react';
import './spinner.scss';

const Spinner = ({ isLoading }) => {
  return (
    <React.Fragment>
      {
        isLoading ?
          <div className='spinnerOverlay'>
            <div className='spinnerContainer' />
          </div>
          : null
      }
    </React.Fragment>
  )
}

export default Spinner;
